<?php$major = 0;$minor = 1;$revision = 2;$build = 0;$rus_arr=array("Янв","Фев","Мар","Апр","Май","Июн","Июл","Авг","Сен", "Окт", "Ноя", "Дек");$eng_arr=array("Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep", "Oct", "Nov", "Dec");$lastupdate = str_ireplace($eng_arr,$rus_arr, date ("d M Y, H:i:s.", filemtime($_SERVER['DOCUMENT_ROOT'].'/game-root/js/application.js')));//setlocale(LC_TIME, 'ru');//$lastupdate = strftime ("%d %b %G %H:%M:%S",filemtime('application.js'));$varr=file($_SERVER['DOCUMENT_ROOT'].'/game-root/lastbuild.txt',FILE_IGNORE_NEW_LINES);if ($varr==false){	$varr=array("124","5490728713","js/application.js;index.php;inc/application.php;inc/login.php");}$ftest=explode(";",$varr[2]);for ($i=0;$i<count($ftest);$i++){	$mtime+=filemtime($_SERVER['DOCUMENT_ROOT']."/game-root/".$ftest[$i]);}if ($mtime>$varr[1]){	$varr[0]+=1;	$varr[1]=$mtime;	$update=true;}$build=$varr[0];if (isset($_GET['short'])==false){	echo("Версия : ".$major.".".$minor.".".$revision." Build:".$build."<br />от ".$lastupdate);} else {	echo("v:".$major.".".$minor.".".$revision.".".$build);}if (isset($update)) file_put_contents($_SERVER['DOCUMENT_ROOT'].'/game-root/lastbuild.txt',implode("\n",$varr));?>